fun processAllSubmissions(): Flow<Result> = flow {
    var offset = 0
    val batchSize = 100

    while (true) {
        val batch = databaseFactory.dbQuery {
            Submissions.selectAll()
                .limit(batchSize, offset.toLong())
                .map { toSubmission(it) }
        }

        if (batch.isEmpty()) break

        batch.forEach { submission ->
            emit(process(submission))
        }

        offset += batchSize
    }
}

