    routing {
        get("/") {
            call.respondText("Hello, World!")
        }

        route("/api") {
            get("/users") {
                call.respond(listOf("User1", "User2"))
            }

            post("/users") {
                val user = call.receive<User>()
                call.respond(HttpStatusCode.Created, user)
            }
        }
    }

    install(ContentNegotiation) {
        json(Json {
            prettyPrint = true
            isLenient = true
        })
    }
}

