fun fetchUser(id: String): Either<ApiError, User> {
    return try {
        Either.Right(api.getUser(id))
    } catch (e: HttpException) {
        Either.Left(ApiError.Network)
    } catch (e: SecurityException) {
        Either.Left(ApiError.Unauthorized)
    } catch (e: Exception) {
        Either.Left(ApiError.Unknown(e.message ?: "unknown"))
    }
}

